import React, { useState, useMemo, useEffect } from 'react';
import './boq.css';
import { Slider, Skeleton, Paper, Button, Typography, Tooltip, Card as MUICard, CardContent, CardActions } from '@mui/material';
import { supabase } from './supabase';

const Card = ({ title, price, image, details, addOns, initialMinimized = false }) => {
  const [selectedAddOns, setSelectedAddOns] = useState({});
  const [isMinimized, setIsMinimized] = useState(initialMinimized);
  const basePrice = price;

  const handleAddOnChange = (addOn, isChecked) => {
    setSelectedAddOns((prevSelectedAddOns) => ({
      ...prevSelectedAddOns,
      [addOn.name]: isChecked ? addOn.price : 0,
    }));
  };

  const calculateTotalPrice = useMemo(() => {
    return Object.values(selectedAddOns).reduce((total, addOnPrice) => total + addOnPrice, basePrice);
  }, [selectedAddOns, basePrice]);

  const toggleMinimize = () => setIsMinimized((prev) => !prev);

  if (isMinimized) {
    return (
      <Paper elevation={3} className="minimized-card" onClick={toggleMinimize}>
        <Typography variant="h6">{title}</Typography>
        <div className="info">
          <Typography variant="body2">Base Price: ₹{basePrice}</Typography>
          <Typography variant="body2">Total Price: ₹{calculateTotalPrice}</Typography>
        </div>
        <Button variant="contained" color="primary" className="start-button">Start</Button>
      </Paper>
    );
  }

  return (
    <MUICard className="card-container" elevation={3}>
      <CardContent>
        <CardSection className="card-image">
          <img src={image} alt={title} className="image" />
        </CardSection>

        <CardSection className="card-features">
          <Typography variant="h6">{title}</Typography>
          <Typography variant="body2">{details}</Typography>
        </CardSection>

        <CardSection className="card-add-ons">
          <Typography variant="h6">ADD ON</Typography>
          <ul>
            {addOns.map((addOn, index) => (
              <li key={index}>
                <label>
                  <input
                    type="checkbox"
                    onChange={(e) => handleAddOnChange(addOn, e.target.checked)}
                  />
                  <Tooltip title={`Add this add-on for ₹${addOn.price}`} arrow>
                    <span>{addOn.name} (+₹{addOn.price})</span>
                  </Tooltip>
                </label>
              </li>
            ))}
          </ul>
        </CardSection>

        <CardSection className="card-summary">
          <Typography variant="h6">Summary</Typography>
          <Typography variant="body2">Base Price: ₹{basePrice}</Typography>
          <Typography variant="body2">Add-Ons: ₹{Object.values(selectedAddOns).reduce((total, price) => total + price, 0)}</Typography>
          <Typography variant="body1">Total Price: ₹{calculateTotalPrice}</Typography>
        </CardSection>
      </CardContent>
      <CardActions>
        <Button variant="contained" color="secondary" onClick={toggleMinimize}>Done</Button>
      </CardActions>
    </MUICard>
  );
};

const CardSection = ({ className, children }) => {
  return <div className={`card ${className}`}>{children}</div>;
};

const App = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [priceRange, setPriceRange] = useState([1000, 5000]);
  const [productsData, setProductData] = useState([]);
  const [loading, setLoading] = useState(true);

  async function getProducts() {
    return await supabase.from("products").select();
  }

  useEffect(() => {
    getProducts().then(async ({ data, error }) => {
      if (error) { 
        console.error(error); 
        return;
      }
      let newData = [];
      for (let ind in data) {
        let obj = data[ind];
        const { data: Photo, error: PhotoError } = await supabase.storage.from("addon").createSignedUrl(`${obj.image}`, 3600);
        if (PhotoError) console.log(PhotoError);
        const { data: addons, error: addonsError } = await supabase.from("addons").select().eq("productid", obj.id);
        let addonsList = [];
        for (let adn in addons) {
          let adf = addons[adn];
          const { data: Photo2, error: PhotoError2 } = await supabase.storage.from("addon").createSignedUrl(`${adf.image}`, 3600);
          if (PhotoError2) console.log(PhotoError2);
          addonsList.push({ ...adf, image: Photo2.signedUrl });
        }
        newData.push({ ...obj, addons: addonsList, image: Photo.signedUrl });
      }
      setProductData(newData);
      setLoading(false);
    });
  }, []);

  const handleSearch = (event) => {
    setSearchQuery(event.target.value.toLowerCase());
  };

  const handleSliderChange = (event, newValue) => {
    setPriceRange(newValue);
  };

  return (
    <div className="App">
      <Paper elevation={3} className="search-filter">
        {loading ? (
          <>
            <Skeleton variant="rectangular" height={40} width="80%" className="skeleton-bar" />
            <Skeleton variant="rectangular" height={40} width="80%" className="skeleton-slider" style={{ marginTop: '10px' }} />
          </>
        ) : (
          <>
            <input
              type="text"
              placeholder="Search products..."
              value={searchQuery}
              onChange={handleSearch}
              className="search-bar"
            />
            <Slider
              value={priceRange}
              onChange={handleSliderChange}
              valueLabelDisplay="auto"
              min={1000}
              max={5000}
              className="price-slider"
            />
          </>
        )}
      </Paper>

      <div className="products-grid">
        {loading ? (
          Array.from({ length: 4 }).map((_, index) => (
            <Skeleton key={index} variant="rectangular" height={200} width="90%" style={{ margin: '10px 0' }} />
          ))
        ) : (
          productsData.map((product, index) => (
            <div key={index}>
              <Card
                title={product.title}
                price={product.price}
                details={product.details}
                addOns={product.addons}
                image={product.image}
                initialMinimized={product.initialMinimized}
              />
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default App;
