import React, { useState, useMemo, useEffect } from 'react';
import { Slider, Skeleton, AppBar, Toolbar, Typography, InputBase, IconButton, Drawer, List, ListItem, ListItemText, Card, CardContent, CardMedia, Chip, Button } from '@mui/material';
import { Search as SearchIcon, Menu as MenuIcon, AddCircleOutline, RemoveCircleOutline } from '@mui/icons-material';
import { supabase } from './supabase';

const ProductCard = ({ product, onToggleAddOn }) => (
  <Card sx={{ display: 'flex', mb: 2, height: 200 }}>
    <CardMedia
      component="img"
      sx={{ width: 200 }}
      image={product.image}
      alt={product.title}
    />
    <CardContent sx={{ flex: '1 0 auto', display: 'flex', flexDirection: 'column', justifyContent: 'space-between' }}>
      <div>
        <Typography component="div" variant="h5">
          {product.title}
        </Typography>
        <Typography variant="subtitle1" color="text.secondary" component="div">
          ₹{product.price}
        </Typography>
      </div>
      <div>
        {product.addons.map((addon) => (
          <Chip
            key={addon.id}
            label={`${addon.name} +₹${addon.price}`}
            onClick={() => onToggleAddOn(product.id, addon)}
            clickable
            color="primary"
            variant="outlined"
            sx={{ mr: 1, mb: 1 }}
          />
        ))}
      </div>
    </CardContent>
  </Card>
);

const App = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [priceRange, setPriceRange] = useState([1000, 12000]);
  const [productsData, setProductData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [drawerOpen, setDrawerOpen] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState('All');
  const [cart, setCart] = useState([]);

  const categories = [
    'All',
    'Furniture', 
    'Civil / Plumbing', 
    'Lighting', 
    'Electrical', 
    'Partitions- door / windows / ceilings',
    'Paint', 
    'HVAC', 
    'Smart Solutions', 
    'Flooring', 
    'Accessories'
  ];

  // Fetch product data (implementation omitted for brevity)

  const filteredProducts = useMemo(() => {
    return productsData.filter((product) => {
      const matchesSearch = product.title.toLowerCase().includes(searchQuery.toLowerCase());
      const matchesPrice = product.price >= priceRange[0] && product.price <= priceRange[1];
      const matchesCategory = selectedCategory === 'All' || product.category === selectedCategory;
      return matchesSearch && matchesPrice && matchesCategory;
    });
  }, [productsData, searchQuery, priceRange, selectedCategory]);

  const handleToggleAddOn = (productId, addon) => {
    setCart((prevCart) => {
      const existingItem = prevCart.find((item) => item.productId === productId);
      if (existingItem) {
        const updatedAddons = existingItem.addons.includes(addon.id)
          ? existingItem.addons.filter((id) => id !== addon.id)
          : [...existingItem.addons, addon.id];
        return prevCart.map((item) =>
          item.productId === productId ? { ...item, addons: updatedAddons } : item
        );
      } else {
        return [...prevCart, { productId, addons: [addon.id] }];
      }
    });
  };

  return (
    <div className="App">
      <AppBar position="static">
        <Toolbar>
          <IconButton
            edge="start"
            color="inherit"
            aria-label="menu"
            onClick={() => setDrawerOpen(true)}
          >
            <MenuIcon />
          </IconButton>
          <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
            Product Catalog
          </Typography>
          <div className="search">
            <InputBase
              placeholder="Search…"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
            <IconButton type="submit" aria-label="search">
              <SearchIcon />
            </IconButton>
          </div>
        </Toolbar>
      </AppBar>

      <Drawer
        anchor="left"
        open={drawerOpen}
        onClose={() => setDrawerOpen(false)}
      >
        <List>
          {categories.map((category) => (
            <ListItem 
              button 
              key={category}
              onClick={() => {
                setSelectedCategory(category);
                setDrawerOpen(false);
              }}
            >
              <ListItemText primary={category} />
            </ListItem>
          ))}
        </List>
      </Drawer>

      <main style={{ padding: '20px' }}>
        <Slider
          value={priceRange}
          onChange={(_, newValue) => setPriceRange(newValue)}
          valueLabelDisplay="auto"
          min={1000}
          max={12000}
          sx={{ mb: 4 }}
        />

        {loading ? (
          <div className="skeleton-container">
            {[...Array(4)].map((_, index) => (
              <Skeleton key={index} variant="rectangular" height={200} sx={{ mb: 2 }} />
            ))}
          </div>
        ) : (
          filteredProducts.map((product) => (
            <ProductCard
              key={product.id}
              product={product}
              onToggleAddOn={handleToggleAddOn}
            />
          ))
        )}
      </main>

      <AppBar position="fixed" color="primary" sx={{ top: 'auto', bottom: 0 }}>
        <Toolbar>
          <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
            Cart Total: ₹{cart.reduce((total, item) => {
              const product = productsData.find(p => p.id === item.productId);
              return total + product.price + item.addons.reduce((addonTotal, addonId) => {
                const addon = product.addons.find(a => a.id === addonId);
                return addonTotal + addon.price;
              }, 0);
            }, 0)}
          </Typography>
          <Button color="inherit">Checkout</Button>
        </Toolbar>
      </AppBar>
    </div>
  );
};

export default App;
