import React, { useState } from 'react';
import './App.css'; // Assuming this is where your CSS is

const RoomDataBox = ({ roomKey, roomValue }) => {
  const [isTooltipVisible, setTooltipVisible] = useState(false);

  const handleBoxClick = () => {
    setTooltipVisible(!isTooltipVisible);
  };

  return (
    <div className="room-data-box-tooltip" onClick={handleBoxClick}>
      <div className="room-data-box">
        <div className="room-data-key">{roomKey}</div>
        <div className="room-data-value">{roomValue}</div>
      </div>
      <div className={`tooltip-text ${isTooltipVisible ? 'active' : ''}`}>
        Extra details about {roomKey}
      </div>
    </div>
  );
};

const RoomDataContainer = ({ roomData }) => {
  return (
    <div className="room-data-box-container">
      {Object.entries(roomData).map(([key, value]) => (
        <RoomDataBox key={key} roomKey={key} roomValue={value} />
      ))}
    </div>
  );
};

export default RoomDataContainer;
