import React, { useState, useMemo, useEffect } from 'react';
import './boq.css';
import { Slider, Skeleton } from '@mui/material';
import { supabase } from './supabase';

const Card = ({ title, price, image, details, addOns, initialMinimized = false, roomData, onAddToCart, onAddToFavorites, onAddToCompare }) => {
  const [selectedAddOns, setSelectedAddOns] = useState({});
  const [isMinimized, setIsMinimized] = useState(initialMinimized);
  const basePrice = price;

  const handleAddOnChange = (addOn, isChecked) => {
    setSelectedAddOns((prevSelectedAddOns) => ({
      ...prevSelectedAddOns,
      [addOn.name]: isChecked ? addOn.price : 0,
    }));
  };

  const calculateTotalPrice = useMemo(() => {
    return Object.values(selectedAddOns).reduce((total, addOnPrice) => total + addOnPrice, basePrice);
  }, [selectedAddOns, basePrice]);

  const toggleMinimize = () => setIsMinimized((prev) => !prev);

  if (isMinimized) {
    return (
      <div className="minimized-card" onClick={toggleMinimize}>
        <span>{title}</span>
        <div className="info">
          <p>Base Price: ‚Çπ{basePrice}</p>
          <p>Total Price: ‚Çπ{calculateTotalPrice}</p>
        </div>
        <button className="start-button">Start</button>
      </div>
    );
  }

  return (
    <div className="card-container">
      <CardSection className="card-image">
        <img src={image} alt={title} className="image" />
      </CardSection>

      <CardSection className="card-features">
        <h3>{title}</h3>
        <p>{details}</p>
        {roomData && (
          <div className="room-info">
            <p>Room Data:</p>
            <ul>
              {Object.entries(roomData).map(([key, value]) => (
                <li key={key}>{`${key}: ${value}`}</li>
              ))}
            </ul>
          </div>
        )}
      </CardSection>

      <CardSection className="card-add-ons">
        <h3>ADD ON</h3>
        <ul>
          {addOns.map((addOn, index) => (
            <li key={index}>
              <label>
                <input
                  type="checkbox"
                  onChange={(e) => handleAddOnChange(addOn, e.target.checked)}
                />
                {addOn.name} (+‚Çπ{addOn.price})
              </label>
            </li>
          ))}
        </ul>
      </CardSection>

      <CardSection className="card-summary">
        <h4>Summary</h4>
        <p>Base Price: ‚Çπ{basePrice}</p>
        <p>Add-Ons: ‚Çπ{Object.values(selectedAddOns).reduce((total, price) => total + price, 0)}</p>
        <p>Total Price: ‚Çπ{calculateTotalPrice}</p>
        <button className="done-button" onClick={toggleMinimize}>Done</button>
      </CardSection>

      <CardSection className="card-actions">
        <button onClick={() => onAddToCart({ title, price: calculateTotalPrice, addOns: selectedAddOns })}>
          üõí Add to Cart
        </button>
        <button onClick={() => onAddToFavorites({ title, price: calculateTotalPrice })}>
          ‚ù§Ô∏è Favorite
        </button>
        <button onClick={() => onAddToCompare({ title, price: calculateTotalPrice, details })}>
          üîÑ Compare
        </button>
      </CardSection>
    </div>
  );
};

const CardSection = ({ className, children }) => {
  return <div className={`card ${className}`}>{children}</div>;
};

const App = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [priceRange, setPriceRange] = useState([1000, 12000]);
  const [productsData, setProductData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [roomNumbers, setRoomNumbers] = useState([]);
  const [activeTab, setActiveTab] = useState(0);
  const [cart, setCart] = useState([]);
  const [favorites, setFavorites] = useState([]);
  const [compareList, setCompareList] = useState([]);
  const [sortOption, setSortOption] = useState('price');

  const categories = [
    'All',
    'Furniture',
    'Civil / Plumbing',
    'Lighting',
    'Electrical',
    'Partitions- door / windows / ceilings',
    'Paint',
    'HVAC',
    'Smart Solutions',
    'Flooring',
    'Accessories'
  ];

  useEffect(() => {
    fetchRoomData();
    fetchProductsData();
  }, []);

  const handleAddToCart = (product) => {
    setCart([...cart, product]);
  };

  const handleAddToFavorites = (product) => {
    setFavorites([...favorites, product]);
  };

  const handleAddToCompare = (product) => {
    if (compareList.length < 3) {
      setCompareList([...compareList, product]);
    } else {
      alert("You can compare up to 3 products at a time.");
    }
  };

  const filteredProducts = useMemo(() => {
    let filtered = productsData.filter((product) => {
      const matchesSearch = product.title.toLowerCase().includes(searchQuery) ||
                            product.details.toLowerCase().includes(searchQuery);
      const matchesPrice = product.price >= priceRange[0] && product.price <= priceRange[1];
      const matchesCategory = categories[activeTab] === 'All' || product.category === categories[activeTab];
      return matchesSearch && matchesPrice && matchesCategory;
    });

    // Sort products
    switch (sortOption) {
      case 'price':
        filtered.sort((a, b) => a.price - b.price);
        break;
      case 'name':
        filtered.sort((a, b) => a.title.localeCompare(b.title));
        break;
      default:
        break;
    }

    return filtered;
  }, [productsData, searchQuery, priceRange, activeTab, sortOption]);

  return (
    <div className="App">
      <div className="search-filter">
        {loading ? (
          <Skeleton variant="rectangular" height={40} width="80%" className="skeleton-bar" />
        ) : (
          <input
            type="text"
            placeholder="Search products..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value.toLowerCase())}
            className="search-bar"
          />
        )}
        <Slider
          value={priceRange}
          onChange={(e, newValue) => setPriceRange(newValue)}
          valueLabelDisplay="auto"
          min={1000}
          max={12000}
          className="price-slider"
        />
      </div>

      <div className="products-grid">
        {loading ? (
          <Skeleton variant="rectangular" height={200} />
        ) : (
          filteredProducts.map(product => (
            <Card
              key={product.id}
              title={product.title}
              price={product.price}
              image={product.image}
              details={product.details}
              addOns={product.addons}
              roomData={roomNumbers[0]}
              onAddToCart={handleAddToCart}
              onAddToFavorites={handleAddToFavorites}
              onAddToCompare={handleAddToCompare}
            />
          ))
        )}
      </div>

      {/* Cart, favorites, compare, etc. can be displayed here as needed */}
    </div>
  );
};

export default App;
