import React, { useState, useEffect } from 'react';
import './boq.css';
import { Slider, Skeleton } from '@mui/material';
import { supabase } from './supabase';

const Card = ({ title, price, image }) => {
  return (
    <div className="card-container">
      <div className="card-image">
        <img src={image} alt={title} className="image" />
      </div>
      <div className="card-features">
        <h3>{title}</h3>
        <p>Price: â‚¹{price}</p>
      </div>
    </div>
  );
};

const App = () => {
  const [productsData, setProductData] = useState([]);
  const [loading, setLoading] = useState(true);

  async function getProducts() {
    return await supabase.from("products").select();
  }

  useEffect(() => {
    getProducts().then(async ({ data, error }) => {
      if (error) {
        console.error(error);
        return;
      }
      let newData = [];
      for (let obj of data) {
        const { data: Photo, error: PhotoError } = await supabase.storage.from("addon").createSignedUrl(`${obj.image}`, 3600);
        if (PhotoError) console.log(PhotoError);

        newData.push({ ...obj, image: Photo.signedUrl });
      }
      setProductData(newData);
      setLoading(false);
    });
  }, []);

  return (
    <div className="App">
      <div className="products-grid">
        {loading ? (
          Array.from({ length: 4 }).map((_, index) => (
            <div key={index} className="card-skeleton-container">
              <Skeleton variant="rectangular" height={150} width="100%" className="skeleton-card-image" />
              <Skeleton variant="text" width="60%" height={20} style={{ margin: '10px 0' }} />
            </div>
          ))
        ) : (
          productsData.map((product, index) => (
            <Card
              key={index}
              title={product.title}
              price={product.price}
              image={product.image}
            />
          ))
        )}
      </div>
    </div>
  );
};

export default App;
