import React, { useState } from 'react';
import './boq.css';

const RoomDataBox = ({ roomData }) => {
  const [activeTooltips, setActiveTooltips] = useState({});

  const handleBoxClick = (key) => {
    setActiveTooltips((prevState) => ({
      ...prevState,
      [key]: !prevState[key], // Toggle the visibility for the clicked box
    }));
  };

  return (
    <div className="room-data-box-container">
      {Object.entries(roomData).map(([key, value]) => (
        <div key={key} className="room-data-box-tooltip" onClick={() => handleBoxClick(key)}>
          <div className="room-data-box">
            <p className="room-data-key">{key}</p>
            <p className="room-data-value">{value}</p>
          </div>
          {activeTooltips[key] && (
            <div className="tooltip-text">
              Additional info about {key}
            </div>
          )}
        </div>
      ))}
    </div>
  );
};

export default RoomDataBox;
