import React, { useState, useMemo, useEffect, useCallback } from 'react';
import { Slider, Skeleton, TextField, Typography, Box } from '@mui/material';
import { styled } from '@mui/system';
import { supabase } from './supabase';

const StyledCard = styled('div')(({ theme }) => ({
  border: `1px solid ${theme.palette.divider}`,
  borderRadius: theme.shape.borderRadius,
  padding: theme.spacing(2),
  marginBottom: theme.spacing(2),
  transition: 'all 0.3s ease',
  '&:hover': {
    boxShadow: theme.shadows[3],
  },
}));

const Card = ({ title, price, image, details, addOns, initialMinimized = false, roomData }) => {
  const [selectedAddOns, setSelectedAddOns] = useState({});
  const [isMinimized, setIsMinimized] = useState(initialMinimized);
  const basePrice = price;

  const handleAddOnChange = useCallback((addOn, isChecked) => {
    setSelectedAddOns((prevSelectedAddOns) => ({
      ...prevSelectedAddOns,
      [addOn.name]: isChecked ? addOn.price : 0,
    }));
  }, []);

  const calculateTotalPrice = useMemo(() => {
    return Object.values(selectedAddOns).reduce((total, addOnPrice) => total + addOnPrice, basePrice);
  }, [selectedAddOns, basePrice]);

  const toggleMinimize = () => setIsMinimized((prev) => !prev);

  if (isMinimized) {
    return (
      <StyledCard onClick={toggleMinimize}>
        <Typography variant="h6">{title}</Typography>
        <Box display="flex" justifyContent="space-between">
          <Typography>Base Price: ₹{basePrice}</Typography>
          <Typography>Total Price: ₹{calculateTotalPrice}</Typography>
        </Box>
        <button className="start-button">Start</button>
      </StyledCard>
    );
  }

  return (
    <StyledCard>
      <img src={image} alt={title} style={{ width: '100%', height: 'auto' }} />
      <Typography variant="h6">{title}</Typography>
      <Typography variant="body2">{details}</Typography>
      {roomData && (
        <Box mt={2}>
          <Typography variant="subtitle1">Room Data:</Typography>
          <ul>
            {Object.entries(roomData).map(([key, value]) => (
              <li key={key}>{`${key}: ${value}`}</li>
            ))}
          </ul>
        </Box>
      )}
      <Box mt={2}>
        <Typography variant="subtitle1">Add-ons:</Typography>
        {addOns.map((addOn, index) => (
          <Box key={index} display="flex" alignItems="center">
            <input
              type="checkbox"
              onChange={(e) => handleAddOnChange(addOn, e.target.checked)}
            />
            <Typography variant="body2">{addOn.name} (+₹{addOn.price})</Typography>
          </Box>
        ))}
      </Box>
      <Box mt={2}>
        <Typography variant="subtitle1">Summary</Typography>
        <Typography>Base Price: ₹{basePrice}</Typography>
        <Typography>Add-Ons: ₹{Object.values(selectedAddOns).reduce((total, price) => total + price, 0)}</Typography>
        <Typography variant="h6">Total Price: ₹{calculateTotalPrice}</Typography>
        <button className="done-button" onClick={toggleMinimize}>Done</button>
      </Box>
    </StyledCard>
  );
};

const App = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [priceRange, setPriceRange] = useState([1000, 5000]);
  const [productsData, setProductData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [roomNumbers, setRoomNumbers] = useState([]);

  const categories = [
    'Furniture', 'Civil / Plumbing', 'Lighting', 'Electrical', 'Partitions- door / windows / ceilings',
    'Paint', 'HVAC', 'Smart Solutions', 'Flooring', 'Accessories'
  ];

  const fetchRoomData = useCallback(async () => {
    try {
      const { data, error } = await supabase
        .from('areas')
        .select()
        .order('created_at', { ascending: false })
        .limit(1);

      if (error) throw error;

      if (data && data.length > 0) {
        const latestRoomData = data[0];
        const roomsArray = Object.entries(latestRoomData).reduce((acc, [key, value]) => {
          if (key !== 'id' && key !== 'created_at') {
            acc[key] = value;
          }
          return acc;
        }, {});
        setRoomNumbers([roomsArray]);
      }
    } catch (error) {
      console.error('Error fetching room data:', error);
    }
  }, []);

  const fetchProductsData = useCallback(async () => {
    try {
      const { data, error } = await supabase
        .from("products")
        .select(`*, addons (*)`);

      if (error) throw error;

      const allImages = data.flatMap(product => [product.image, ...product.addons.map(addon => addon.image)]);
      const uniqueImages = [...new Set(allImages)];

      const { data: signedUrls, error: signedUrlError } = await supabase.storage
        .from("addon")
        .createSignedUrls(uniqueImages, 3600);

      if (signedUrlError) throw signedUrlError;

      const urlMap = Object.fromEntries(signedUrls.map(item => [item.path, item.signedUrl]));

      const processedData = data.map(product => ({
        ...product,
        image: urlMap[product.image] || '',
        addons: product.addons.map(addon => ({
          ...addon,
          image: urlMap[addon.image] || ''
        }))
      }));

      setProductData(processedData);
    } catch (error) {
      console.error('Error fetching products data:', error);
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    Promise.all([fetchRoomData(), fetchProductsData()]);
  }, [fetchRoomData, fetchProductsData]);

  const handleSearch = (event) => {
    setSearchQuery(event.target.value.toLowerCase());
  };

  const handleSliderChange = (event, newValue) => {
    setPriceRange(newValue);
  };

  const filteredProducts = useMemo(() => {
    return productsData.filter(product => 
      product.title.toLowerCase().includes(searchQuery) &&
      product.price >= priceRange[0] &&
      product.price <= priceRange[1]
    );
  }, [productsData, searchQuery, priceRange]);

  return (
    <Box sx={{ maxWidth: 1200, margin: 'auto', padding: 3 }}>
      <Box mb={3}>
        {loading ? (
          <>
            <Skeleton variant="rectangular" height={40} width="100%" sx={{ mb: 2 }} />
            <Skeleton variant="rectangular" height={40} width="100%" />
          </>
        ) : (
          <>
            <TextField
              fullWidth
              label="Search products"
              variant="outlined"
              value={searchQuery}
              onChange={handleSearch}
              sx={{ mb: 2 }}
            />
            <Typography gutterBottom>Price Range</Typography>
            <Slider
              value={priceRange}
              onChange={handleSliderChange}
              valueLabelDisplay="auto"
              min={1000}
              max={5000}
            />
          </>
        )}
      </Box>

      {loading ? (
        <Box display="grid" gridTemplateColumns="repeat(auto-fill, minmax(250px, 1fr))" gap={3}>
          {Array.from({ length: 4 }).map((_, index) => (
            <Skeleton key={index} variant="rectangular" height={300} />
          ))}
        </Box>
      ) : (
        categories.map((category) => {
          const categoryProducts = filteredProducts.filter(product => product.category === category);
          if (categoryProducts.length === 0) return null;

          return (
            <Box key={category} mb={4}>
              <Typography variant="h5" gutterBottom>{category}</Typography>
              <Box display="grid" gridTemplateColumns="repeat(auto-fill, minmax(250px, 1fr))" gap={3}>
                {categoryProducts.map((product) => (
                  <Card
                    key={product.id}
                    title={product.title}
                    price={product.price}
                    details={product.details}
                    addOns={product.addons}
                    image={product.image}
                    initialMinimized={product.initialMinimized}
                    roomData={roomNumbers[0]}
                  />
                ))}
              </Box>
            </Box>
          );
        })
      )}
    </Box>
  );
};

export default App;
